import React from "react";
import memesData from "../memesData";


export default function Meme() {
    const[memeImage, urlLink] = React.useState(
        meme = {
        topText: "",
        bottomText: "",
        randomImage: "http://i.imgflip.com/lbij.jpg"

    })

    const[allMemeImages,setAllMemeImages] = React.useState(memesData)

    function getMeme () {
        console.log("CLICKED")
        const theMeme = memesData.data.memes
        const randomNumber = Math.floor(Math.random() * theMeme.length)
        
        urlLink(theMeme[randomNumber].url)
       
        
        
    }


    return (
        <div className="form">

            <div className="labels">
            <input type="text" placeholder="Top text"></input> 
            <input type="text" placeholder="Bottom text"></input>
            </div>
            <div className="btnContainer">
            <button onClick={getMeme}> Get a new meme image</button>
            </div>
            <div className="imgContainer">
            <img src={memeImage} alt="" className="memeImg"/>
            </div>
            

        </div>
    )
}
