import React from "react";
import memesData from "../memesData";


export default function Meme() {
   // const[memeImage, urlLink] = React.useState("")

   const [meme, setMeme] = React.useState({
            topText: "",
            bottomText: "",
            randomImage: "http://i.imgflip.com/lbij.jpg"

        }
   )

   const [allMemeImages, setAllMemeImages] = React.useState(memesData)

    function getMeme () {
        console.log("CLICKED")
        const theMeme = allMemeImages.data.memes
        const randomNumber = Math.floor(Math.random() * theMeme.length)
        const url = theMeme[randomNumber].url
        setMeme(prevMeme => ({
            ...prevMeme, //pulling all of the properties of previous Meme
            randomImage: url
        }))
       
        
        
    }

    function InputText (event) {
        const [type,placeholder,onChange,name,value] = event.target
    }


    return (
        <div className="form">

            <div className="labels">

            <input
             type="text" 
             placeholder="Top text"
             onChange={InputText}
             name="topText"
             value={meme.topText}
             />
            

            <input
             type="text" 
             placeholder="Bottom text"
             onChange={InputText}
             name="bottomText"
             value={meme.bottomText}
            />

             
            
            </div>
            <div className="btnContainer">
            <button onClick={getMeme}> Get a new meme image</button>
            </div>

            <div className="imgContainer">
            <img src={meme.randomImage} alt="" className="memeImg"/>
            <h2 className="toptxt" > ONE SIMPLY DOES NOT</h2>
            <h2 className="bottomtxt" > WALK INTO THE MORDOR</h2>
            </div>
            

        </div>
    )
}
