import React from "react";

export default function Cards (props) {

    let badgeText 
    if (props.openSpots === 0) {
        badgeText = "SOLD OUT"
    } else if ( props.location === "Online") {
        badgeText = "ONLINE"
    }


        return(
            <div className="cards">

                <p className="status">{props.status}</p>

                <img src={props.coverImg} className="mainPic"/>
                
                <div className="under">
               
                <p> <img src={props.star} alt="" /> {props.grade} {props.country} </p>
                <p> {props.title} </p>
                <p> {props.price}</p>
                <p> {badgeText && <div className=""> {badgeText} </div>} </p>
                <p> {props.openSpots === 0 && <div className=""> {badgeText} </div>} </p>

                    
                </div>

                
                        
            </div>

        )
    
}
